package com.metao.java8.hackerrank;

public class PolynomialTest {

    public static void main(String[] args) {
        final String text = "";
        final String text2 = "ababaa";
        int removeIndex = makePolynomialMethod1(text);
        System.out.println(removeIndex);
        for (int i = 0; i < text.length(); i++) {
            if (isPolynomial(text, i)) {
                System.out.println(text.charAt(i));
            }
        }
    }

    private static int makePolynomialMethod1(String text) {
        String temp = text.replaceAll("\\s+", "");
        for (int i = 0; i < temp.length() / 2; i++) {
            int index = temp.length() - i - 1;
            if (temp.charAt(i) != temp.charAt(index)) {
                if (isPolynomial(temp, i)) {
                    return i;
                }
                if (isPolynomial(temp, index)) {
                    System.out.println(temp);
                    System.out.println(index);
                    return index;
                }
            }
        }
        return -1;
    }

    private static boolean isPolynomial(String text, int ignoreIndex) {
        int j = text.length() - 1, i = 0;
        while (i <= j) {
            if (i == ignoreIndex) {
                i++;
            } else if (j == ignoreIndex) {
                j--;
            }
            if (text.charAt(i) != text.charAt(j)) {
                return false;
            }
            i++;
            j--;
        }
        return true;
    }
}
